import { Component, Input, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, ValidationErrors } from '@angular/forms';
import { MatCheckbox } from '@angular/material/checkbox';
import { MatFormFieldModule, MatLabel } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { UntilDestroy, untilDestroyed } from '@ngneat/until-destroy';
import { tap } from 'rxjs';
import { PolarisIcon } from '../polaris-icon';
import { PolarisUiFormBase } from '../polaris-ui-form-base';

@UntilDestroy()
@Component({
  selector: 'polaris-input',
  standalone: true,
  imports: [
    CommonModule,
    PolarisIcon,
    MatLabel,
    MatFormFieldModule,
    MatInputModule,
    ReactiveFormsModule,
    MatCheckbox,
  ],
  templateUrl: './polaris-input.component.html',
  styleUrl: './polaris-input.component.scss',
})
export class PolarisInput extends PolarisUiFormBase implements OnInit {
  @Input() public infoTooltip!: string;

  public override ngOnInit(): void {
    super.ngOnInit();
    this._subscribeToFormControlEvents();
    this._setPlaceholder();
    this._setTestId();
  }

  /**
   * Sets the default placeholder based on the input type.
   *
   * @remarks
   * This method checks the value of the `type` property and sets the `placeholder` property accordingly.
   * If the `type` is 'phone', the placeholder is set to '(000) 000-0000'.
   * If the `type` is not 'phone', the placeholder remains unchanged
   * (Currently, only 'phone' is supported).
   *
   * @returns {void}
   */
  // TODO: Add other default placeholders based on the input type
  private _setPlaceholder(): void {
    if (this.type === 'phone') {
      this.placeholder = '(000) 000-0000';
    }
  }

  private _setTestId(): void {
    this.testId = `polaris-input-` + this.label
      .toLowerCase()
      .replace(/\s+/g, '-') // Replace spaces with hyphens
      .replace(/[^a-z0-9-]/g, '') // Remove special characters
      .trim();
  }

  /**
   * Subscribes to form control events and processes errors when the form control becomes invalid.
   *
   * @remarks
   * This method sets up a subscription to the `events` observable of the form control.
   * When the form control becomes invalid, it calls the `_getErrorMessages` method with the form control errors.
   * The subscription is automatically cleaned up when the component is destroyed using the `untilDestroyed` operator.
   *
   */
  private _subscribeToFormControlEvents(): void {
    this.formControl.events.pipe(
      tap((): void => {
        if (this.formControl.invalid) {
          this.getErrorMessages(this.formControl.errors as ValidationErrors);
        }
      }),
      untilDestroyed(this),
    ).subscribe();
  }
}

