import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlEvent, ReactiveFormsModule, ValidationErrors, ValueChangeEvent } from '@angular/forms';
import { MatError, MatLabel } from '@angular/material/form-field';
import { MatRadioModule } from '@angular/material/radio';
import { UntilDestroy, untilDestroyed } from '@ngneat/until-destroy';
import { tap } from 'rxjs';
import { PolarisIcon } from '../polaris-icon';
import { PolarisGroupOption, PolarisUiFormBase } from '../polaris-ui-form-base';

/**
 * @description A component for rendering a group of radio buttons.
 *
 * @template T - The TYPE of the custom 'data' field in the PolarisGroupOption Object.
 *
 *
 * @example
 * <polaris-radio-group
 *    ui-label="Select permissions based on:"
 *    [options]="newPermissionsOptions"
 *    [ui-form-control]="newPermissionsFormControl"
 *    ui-orientation="vertical">
 * </polaris-radio-group>
 *
 *
 * @note PolarisRadioGroup uses the PolarisUiBase component to inherit its base styles and properties.
 *
 * @note PolarisRadioGroup uses the ReactiveFormsModule to handle form control functionality.
 *
 * @note PolarisRadioGroup uses the MatLabel and MatRadioModule to provide the necessary UI components.
 *
 */
@UntilDestroy()
@Component({
  selector: 'polaris-radio-group',
  standalone: true,
  imports: [
    CommonModule,
    MatLabel,
    MatRadioModule,
    ReactiveFormsModule,
    MatError,
    PolarisIcon,
  ],
  templateUrl: './polaris-radio-group.component.html',
  styleUrl: './polaris-radio-group.component.scss',
})
export class PolarisRadioGroup<T> extends PolarisUiFormBase implements OnInit {
  /**
   * An array of radio button options.
   */
  @Input() public options: PolarisGroupOption<T>[] = [];

  /**
   * Whether to display values as icons
   * @default false
   */
  @Input() public displayOnly: boolean = false;

  /**
   * Whether to display truthy values as a checkmark () icon
   * @default true
   */
  @Input() public displayTruthyValues: boolean = true;

  /**
   * Whether to display falsy values as a close () icon
   * @default false
   */
  @Input() public displayFalsyValues: boolean = false;

  /**
   * Outputs the new array of options with the updated selections.
   * In case you need to bind to the selected options instead of a form control
   */
  @Output() public onSelectionChange: EventEmitter<ValueChangeEvent<T>> = new EventEmitter<ValueChangeEvent<T>>();

  /**
   * The CSS class for the radio group's orientation.
   * Default value is 'align-items-center'.
   */
  public orientationClass: string = 'align-items-center';
  public labelOrientationClass: string = 'align-items-start flex-column';

  /**
   * Initializes the component.
   */
  public override ngOnInit(): void {
    super.ngOnInit();
    this._setRadioGroupOrientationClass();
    this._subscribeToControlEvents();
  }

  /**
   * Sets the CSS classes for the radio group's orientation and label based on the component's properties.
   *
   * @description
   * This function updates the `orientationClass` and `labelOrientationClass` properties based on the
   * `orientation` and `stackLabel` properties of the component.
   *
   * @returns {void}
   *
   * @note
   * The `orientationClass` is set to 'align-items-center' when the `orientation` property is 'horizontal',
   * and 'align-items-start flex-column' otherwise.
   *
   * The `labelOrientationClass` is set to 'align-items-start flex-column' when the `stackLabel` property is true,
   * and 'align-items-center' otherwise.
   */
  public _setRadioGroupOrientationClass(): void {
    this.labelOrientationClass = this.stackLabel ? 'align-items-start flex-column' : 'align-items-center';
    this.orientationClass = this.orientation === 'horizontal' ? 'align-items-center' : 'align-items-start flex-column';
  }

  private _subscribeToControlEvents(): void {
    this.formControl.events.pipe(
      tap((event: ControlEvent): void => {

        if (event instanceof ValueChangeEvent) {
          this.onSelectionChange.emit(event);
        }

        if (this.formControl.invalid) {
          this.getErrorMessages(this.formControl.errors as ValidationErrors);
        }
      }),
      untilDestroyed(this),
    ).subscribe();
  }
}

