  selector: 'polaris-rich-text-editor',
  standalone: true,
  imports: [CommonModule, EditorComponent, ReactiveFormsModule, MatLabel, MatError],
  providers: [
    {
      provide: TINYMCE_SCRIPT_SRC,
      useFactory: () => inject(POLARIS_RICH_TEXT_EDITOR_SRC_URL_TOKEN),
