import { Component, ElementRef, EventEmitter, Input, Output, ViewChild } from '@angular/core';
import { CommonModule } from '@angular/common';
import {
  PolarisFilePickerFile,
  PolarisFilePickerStatus,
  PolarisFilePickerFileExtension,
  PolarisFilePickerError
} from './polaris-file-picker.type';
import { MatProgressBarModule } from '@angular/material/progress-bar';
import { PolarisUiFormBase } from '../polaris-ui-form-base';
import { PolarisIcon } from '../polaris-icon/polaris-icon.component';
import { FileSizePipe } from '@dealer-portal/polaris-core';
import { PolarisIconButton } from '../polaris-icon-button';

@Component({
    selector: 'polaris-file-picker',
    imports: [CommonModule, MatProgressBarModule, PolarisIcon, FileSizePipe, PolarisIconButton],
    templateUrl: './polaris-file-picker.component.html',
    styleUrl: './polaris-file-picker.component.scss'
})
export class PolarisFilePicker<T> extends PolarisUiFormBase<T> {
  /**
   * Allow multiple file selection
   */
  @Input() allowMultiple: boolean = false;

  /**
   * Allows consecutive uploads without resetting the files (only relevant if allowMultiple is false).
   */
  @Input() allowConsecutive: boolean = false;

  /**
   * Allowed file types for selection. An empty list indicates any file type is allowed.
   */
  @Input() allowedFileTypes: PolarisFilePickerFileExtension[] = [];

  /**
   * Maximum file size in bytes. A value of 0 indicates no limit.
   */
  @Input() maximumFileSize: number = 5 * 1024 * 1024; // 5MB default

  /**
   * List of files to be displayed with an upload status.
   */
  @Input() uploadFiles: PolarisFilePickerFile[] = [];

  /**
   * Event emitted when files are added.
   */
  @Output() onAddFiles = new EventEmitter<File[]>();

  /**
   * Event emitted when a file upload is canceled.
   */
  @Output() onCancelFile = new EventEmitter<PolarisFilePickerFile>();

  /**
   * Event emitted when a file is deleted.
   */
  @Output() onDeleteFile = new EventEmitter<PolarisFilePickerFile>();


  @ViewChild('fileInput') fileInput!: ElementRef<HTMLInputElement>;

  readonly fileStatus = PolarisFilePickerStatus;

  /**
   * Validates a file based on allowed file types and maximum file size.
   * @param file The file to validate.
   * @returns An object containing the validation result and any errors.
   */
  private _validateFile(file: File): {
    isValid: boolean;
    errors: { invalidFileType: boolean; fileSizeExceeded: boolean };
  } {
    let isValid = true;
    const errors = {
      invalidFileType: false,
      fileSizeExceeded: false,
    };

    // Validate file type
    if (this.allowedFileTypes.length > 0) {
      const fileType = file.name.split('.').pop()?.toLowerCase();
      if (!this.allowedFileTypes.includes(fileType as PolarisFilePickerFileExtension)) {
        errors.invalidFileType = true;
        isValid = false;
      }
    }

    // Validate file size
    if (this.maximumFileSize > 0 && file.size > this.maximumFileSize) {
      errors.fileSizeExceeded = true;
      isValid = false;
    }

    return { isValid, errors };
  }

  /**
   * Handles the addition of files, validates them, emits the valid files, and creates errors for invalid files.
   * @param files The files to add.
   */
  private _onAddFiles(files: File[]): void {
    // Clear previous errors
    this.formControl.setErrors(null);

    // Keep track of invalid file names
    const invalidFileTypes: string[] = [];
    const fileSizeExceeded: string[] = [];

    if (!this.allowMultiple && files.length > 0) {
      // Don't allow more than one file to be displayed if multiple files are not allowed
      if (!this.allowConsecutive && this.uploadFiles.length > 0) {
        files = [];
      } else {
        files = [files[0]];
      }
    }

    const outputFiles = files
      .map((file) => {
        const { isValid, errors } = this._validateFile(file);
        if (!isValid) {
          if (errors.invalidFileType) {
            invalidFileTypes.push(file.name);
          }
          if (errors.fileSizeExceeded) {
            fileSizeExceeded.push(file.name);
          }
          return null;
        }
        return file;
      })
      .filter((file) => file !== null) as File[];

    if (invalidFileTypes.length > 0 || fileSizeExceeded.length > 0) {
      // Override the errors whenever new files are uploaded
      this.formControl.setErrors({
        [PolarisFilePickerError.InvalidFileType]: invalidFileTypes, [PolarisFilePickerError.FileSizeExceeded]: fileSizeExceeded
      });
    }

    this.onAddFiles.emit(outputFiles);
  }

  /**
   * Handles the file input change event.
   * @param event The input event.
   */
  public onInputFile(event: Event): void {
    const inputElement = event.currentTarget as HTMLInputElement;
    const selectedFiles = Array.from(inputElement.files ?? []) as File[];
    this._onAddFiles(selectedFiles);
    inputElement.value = '';
  }

  /**
   * Handles the click event on the file picker.
   * @param event The mouse event.
   */
  public onClickFilePicker(event?: MouseEvent): void {
    this.fileInput.nativeElement.click();
    if (event) {
      this.onClick.emit(event);
    }
  }

  /**
   * Handles the keydown event for accessibility.
   * @param event The keyboard event.
   */
  public onKeyDown(event: KeyboardEvent): void {
    if (event.key === 'Enter' || event.key === ' ') {
      event.preventDefault();
      this.onClickFilePicker();
    }
  }

  /**
   * Handles drag events for the file picker.
   * @param event The drag event.
   */
  public onDrag(event: DragEvent): void {
    event.preventDefault();
    event.stopPropagation();
    const dropArea = event.currentTarget as HTMLElement;
    if (event.type === 'dragover') {
      dropArea.classList.add('drag-over');
    } else if (event.type === 'dragleave' || event.type === 'drop') {
      dropArea.classList.remove('drag-over');
    }
  }

  /**
   * Handles the drop event for the file picker.
   * @param event The drag event.
   */
  public onDrop(event: DragEvent): void {
    this.onDrag(event);
    this._onAddFiles(Array.from(event.dataTransfer?.files ?? []));
  }

  /**
   * Handles the cancellation of a file.
   * @param file The file to cancel.
   */
  public onCancel(file: PolarisFilePickerFile): void {
    this.onCancelFile.emit(file);
  }

  /**
   * Handles the deletion of a file.
   * @param file The file to delete.
   */
  public onDelete(file: PolarisFilePickerFile): void {
    this.onDeleteFile.emit(file);
  }

  /**
   * Returns the CSS classes for the file picker.
   */
  public get filePickerClass() {
    return {
      vertical: this.orientation === 'vertical',
      [this.customClass]: this.customClass,
    };
  }
}

import {
  AfterViewInit,
  Component,
  ElementRef,
  EmbeddedViewRef, Input,
  Renderer2,
  TemplateRef,
  ViewChild,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { PolarisUiBase } from '../polaris-ui-base';
import { PolarisHeadingLevel } from './polaris-heading.type';

@Component({
    selector: 'polaris-heading',
    imports: [CommonModule],
    templateUrl: './polaris-heading.component.html',
    styleUrl: './polaris-heading.component.scss'
})
export class PolarisHeading extends PolarisUiBase implements AfterViewInit {
  @Input() color: 'primary' | 'default' = 'default';

  @ViewChild('headingTemplate', { read: TemplateRef, static: true }) headingTemplate!: TemplateRef<unknown | null>;
  public headingLevel: PolarisHeadingLevel = 'h1'; // Default to <h1>

  constructor(
    private _elementRef: ElementRef,
    private _renderer: Renderer2,
  ) {
    super();
  }

  public ngAfterViewInit(): void {

    // Set the heading level
    this.headingLevel = this._detectHeadingLevel();

    // Add the color class to the heading element
    const colorClass: string = this._getColorClass(this.color);

    // Make a new heading element
    const classList: string = `${this.customClass} display-${this.headingLevel} ${colorClass}`.trim();
    const headingElement: HTMLHeadingElement = this._renderer.createElement(this.headingLevel);
    headingElement.setAttribute('class', classList);
    headingElement.setAttribute('data-test-id', this.testId);

    // Add the new heading element to the template
    this._renderer.appendChild(this._elementRef.nativeElement, headingElement);

    // Re-Render the template
    if (this.headingTemplate) {
      const embeddedView: EmbeddedViewRef<unknown | null> = this.headingTemplate.createEmbeddedView(null);
      embeddedView.rootNodes.forEach(node => this._renderer.appendChild(headingElement, node));
      embeddedView.detectChanges();
    }
  }

  private _getColorClass(color: string): string {
    switch (color) {
      case 'primary':
        return 'text-primary';
      default:
        return '';
    }
  }

  private _detectHeadingLevel(): PolarisHeadingLevel {

    // Iterate over possible heading levels from 1 to 6
    for (let i = 1; i <= 6; i++) {
      const headingLevel: PolarisHeadingLevel = `h${i}` as PolarisHeadingLevel;

      if (this._elementRef.nativeElement.hasAttribute(headingLevel)) {
        return headingLevel;
      }
    }

    return this.headingLevel; // Default to <h1> if no specific attribute is found
  }
}

import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { PolarisUiBase } from '../polaris-ui-base';

/**
 * A component for rendering a hyperlink with customizable styling and behavior.
 *
 * @example
 * <polaris-href [underline]="false">Custom Link</polaris-href>
 */
@Component({
  selector: 'polaris-href',
  imports: [CommonModule],
  templateUrl: './polaris-href.component.html',
  styleUrl: './polaris-href.component.scss',
})
export class PolarisHref extends PolarisUiBase {
  /**
   * Determines whether the link should have an underline.
   *
   * @default true
   */
  // TODO: This should be 'hover', 'always', true, or 'none'/false
  @Input() underline: boolean = true;

  /**
   * The URL to navigate to when the link is activated.
   * If omitted, the component will behave like a button (still look like a link) and only emit click events.
   */
  @Input() url: string | null = null;

  /**
   * Shortcut to open link in a new browser tab (sets target and rel attributes appropriately).
   */
  @Input() openInNewTab: boolean = false;

  /**
   * Explicit target attribute to use. Ignored if openInNewTab is true.
   */
  @Input() target: '_self' | '_blank' | '_parent' | '_top' | string | null = null;

  /**
   * rel attribute for the anchor element (security / SEO). Auto-populated with "noopener noreferrer" when openInNewTab is true.
   */
  @Input() rel: string | null = null;

  /**
   * Optional download filename; if provided this behaves as a download link.
   */
  @Input() download: string | null = null;

  /**
   * Whether to prevent navigation when disabled.
   */
  @Input() preventNavigationWhenDisabled: boolean = true;

  /** Computed href value for template binding */
  get href(): string | null {
    if (!this.url) return null; // allow button-like mode
    return this.url;
  }

  /** Computed target attribute */
  get computedTarget(): string | null {
    if (this.openInNewTab) return '_blank';
    return this.target;
  }

  /** Computed rel attribute with security additions for new tab */
  get computedRel(): string | null {
    if (this.openInNewTab) {
      // Merge existing rel if user supplied any portion
      const base = 'noopener noreferrer';
      if (this.rel) {
        // ensure required tokens present
        const tokens = new Set((this.rel + ' ' + base).split(/\s+/).filter(Boolean));
        return Array.from(tokens).join(' ');
      }
      return base;
    }
    return this.rel;
  }

  /** Whether element should have role=button instead of default link semantics */
  get isButtonLike(): boolean {
    return !this.href;
  }

  /** Prevent navigation when disabled */
  override emitClickEvent(event: MouseEvent): void {
    if (this.disabled && this.preventNavigationWhenDisabled) {
      event.preventDefault();
      event.stopPropagation();
    }
    super.emitClickEvent(event);
  }

  /** Handles keydown events for accessibility */
  handleKeyDown(event: KeyboardEvent) {
    if (event.key === 'Enter' || event.key === ' ') {
      event.preventDefault(); // Prevent scrolling on Space
      this.emitClickEvent(new MouseEvent('click')); // Trigger a click event
    }
  }
}

import { Overlay } from '@angular/cdk/overlay';
import {
  AfterViewInit,
  Component,
  ElementRef,
  EmbeddedViewRef,
  Injector,
  Input,
  Renderer2,
  TemplateRef,
  ViewChild,
  ViewContainerRef,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { polarisDefaultTooltipDelay, PolarisTooltipDirective } from '../polaris-tooltip';
import { PolarisUiBase } from '../polaris-ui-base';
import { PolarisIconColor } from './polaris-icon-color.type';
import { PolarisIconSize } from './polaris-icon-size.type';


@Component({
    selector: 'polaris-icon',
    imports: [CommonModule],
    templateUrl: './polaris-icon.component.html',
    styleUrl: './polaris-icon.component.scss'
})
export class PolarisIcon extends PolarisUiBase implements AfterViewInit {
  @ViewChild('iconTemplate', { read: TemplateRef, static: true }) iconTemplate!: TemplateRef<unknown>;

  @Input() color: string | PolarisIconColor= '';
  @Input() size: string | PolarisIconSize = 'md';
  @Input() tooltipShowDelay: number = polarisDefaultTooltipDelay;
  @Input() tooltipHideDelay: number = polarisDefaultTooltipDelay;

  constructor(
    private _elementRef: ElementRef,
    private _renderer: Renderer2,
    private _viewContainer: ViewContainerRef,
    private _injector: Injector,
  ) {
    super();
  }

  public ngAfterViewInit(): void {
    // Make a new <i> element
    const iconElement: HTMLElement = this._renderer.createElement('i');
    iconElement.setAttribute('data-test-id', this.testId);

    // Add tooltip dynamically
    if (this.tooltip) {
      this._attachTooltip(iconElement, this.tooltip);
    }

    // Add the new <i> element to the template
    this._renderer.appendChild(this._elementRef.nativeElement, iconElement);

    // Re-Render the template
    if (this.iconTemplate) {
      const embeddedView: EmbeddedViewRef<unknown> = this.iconTemplate.createEmbeddedView(null);
      embeddedView.rootNodes.forEach((node) => {
        if (node.nodeType === Node.TEXT_NODE && node.textContent?.trim()) {
          // Extract the icon name from the template's content
          const iconGlyphClass: string = `polaris-icon-${node.textContent.trim()}`;
          const iconSizeClass: string = `polaris-icon-size-${this.size}`;
          const iconColorClass: string = this.color
            ? `polaris-icon-color-${this.color}`
            : '';
          const classList = `polaris-icon ${iconGlyphClass} ${iconSizeClass} ${iconColorClass} ${this.customClass}`.trim();

          // Set the class attribute on the <i> element
          iconElement.setAttribute('class', classList);
        }
      });
      embeddedView.detectChanges();
    }
  }

    /**
     * Attaches a tooltip to a specified HTML element using the PolarisTooltipDirective.
     *
     * @param element - The HTML element to which the tooltip will be attached.
     * @param tooltipContent - The content to be displayed within the tooltip.
     * @returns void
     */
  private _attachTooltip(element: HTMLElement, tooltipContent: string): void {
    const directive: PolarisTooltipDirective = new PolarisTooltipDirective(
      this._viewContainer,
      new ElementRef(element),
      this._injector.get(Overlay),
    );

    // Set any inputs required by the directive
    directive.tooltipHtml = tooltipContent;

    // Pass in optional inputs
    directive.tooltipShowDelay = this.tooltipShowDelay;
    directive.tooltipHideDelay = this.tooltipHideDelay;

    this._renderer.listen(element, 'mouseenter', (): void => {
      directive.onMouseEnter();
    });
    this._renderer.listen(element, 'mouseleave', (): void => {
      directive.onMouseLeave();
    });
    this._renderer.listen(element, 'focus', (): void => {
      directive.onFocus();
    });
    this._renderer.listen(element, 'blur', (): void => {
      directive.onBlur();
    });
  }
}

